送ります。","  end","","  def save_notification_comment!(current_user, comment_id, visited_id)","    notification = current_user.active_notifications.new(","      book_id: id,","      comment_id: comment_id,","      visited_id: visited_id,","      action: 'comment'","    )","    if notification.visitor_id == notification.visited_id","      notification.checked = true","    end","    notification.save if notification.valid?","  end"],"id":60,"ignore":true}]]},"ace":{"folds":[],"scrolltop":226,"scrollleft":0,"selection":{"start":{"row":26,"column":0},"end":{"row":26,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":13,"mode":"ace/mode/ruby"}},"timestamp":1664436719522,"hash":"fac4a057f4eab1b78fade5883c5418619f4a652b"}